	})})$(".flp input").blur(function(){	//animate the label down if content of the input is empty	if($(this).val() == "")	{		$(this).next().removeClass("focussed").children().stop(true).each(function(i){			d = i*50;			$(this).delay(d).animate({top: 0}, 500, 'easeInOutBack');		})	}})</script><div id="events">	<?php include 'events.php';?></div><br><br><br><div id="gallery">	<h1 style="color:white;margin-left:-50vw; font-size:40px;">Gallery</h1>	<?php include 'gallery.php' ;?></div><?php include 'footer.php';?></html>